import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import ParticlesTest from "./ParticlesTest"
//import Particles from "@tsparticles/react";
//import { loadFull } from "tsparticles";
//currentNum = parseInt(localStorage.getItem('number')) || 0



/*
function MyButton() {
  const num = parseInt(localStorage.getItem('number')) || 0
  localStorage.setItem('number',JSON.stringify(num + 1))
  return (
    <button>I'm a button</button>
  );
}
  */
//const num = parseInt(localStorage.getItem('number'));
function App() {
  const [init, setInit] = useState(false);

  // this should be run only once per application lifetime
  useEffect(() => {
    initParticlesEngine(async (engine) => {
      // you can initiate the tsParticles instance (engine) here, adding custom shapes or presets
      // this loads the tsparticles package bundle, it's the easiest method for getting everything ready
      // starting from v2 you can add only the features you need reducing the bundle size
      //await loadAll(engine);
      //await loadFull(engine);
      await loadSlim(engine);
      //await loadBasic(engine);
    }).then(() => {
      setInit(true);
    });
  }, []);

  const [currentNum, updateCurrentNum] = useState(parseInt(localStorage.getItem('number')) || 0);

function incrementNumber(event) {


    if(event.shiftKey)
    {
      updateCurrentNum(currentNum + 5)
    }else
    {
      updateCurrentNum(currentNum + 1)
    }
  //const num = parseInt(localStorage.getItem('number')) || 0

  localStorage.setItem('number',JSON.stringify(currentNum + (event.shiftKey ? 5 : 1)))

}
function decrementNumber(event) {

  //const num = parseInt(localStorage.getItem('number')) || 0
  if(currentNum <= 0)
  {
    return
  }

  if(event.shiftKey)
  {
    if(currentNum <= 5)
    {
      updateCurrentNum(0)
    }else
    {
      updateCurrentNum(currentNum - 5)
    }
  }else
  {
    updateCurrentNum(currentNum - 1)
  }

  localStorage.setItem('number',JSON.stringify(currentNum - (event.shiftKey ? 5 : 1)))

}
  return (
    <div className="App">
      <title>First web page!</title>
      <ParticlesTest id="particles"/>
      <h1 className='Menu-bar'>Some sample text for a page</h1>

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>

      </header>


      <p>
      <button onClick={incrementNumber}>+</button>
      <span style={{ padding: "0 10px" }}>{currentNum}</span>

      <button onClick={decrementNumber}>-</button>
      </p>
      <input type='text' onChange={incrementNumber}></input>

      <p>after</p>
    </div>
  );
}

export default App;
