import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import ParticlesTest from "./particlesTest";
import Particles from "@tsparticles/react";
import { loadFull } from "tsparticles";
//currentNum = parseInt(localStorage.getItem('number')) || 0



/*
function MyButton() {
  const num = parseInt(localStorage.getItem('number')) || 0
  localStorage.setItem('number',JSON.stringify(num + 1))
  return (
    <button>I'm a button</button>
  );
}
  */
//const num = parseInt(localStorage.getItem('number'));
function App() {
  const particlesInit = async (main) => {
    // Load the full tsparticles package
    await loadFull(main);
  };
  const [currentNum, updateCurrentNum] = useState(parseInt(localStorage.getItem('number')) || 0);

function incrementNumber(event) {


    if(event.shiftKey)
    {
      updateCurrentNum(currentNum + 5)
    }else
    {
      updateCurrentNum(currentNum + 1)
    }
  //const num = parseInt(localStorage.getItem('number')) || 0

  localStorage.setItem('number',JSON.stringify(currentNum + (event.shiftKey ? 5 : 1)))

}
function decrementNumber(event) {

  //const num = parseInt(localStorage.getItem('number')) || 0
  if(currentNum <= 0)
  {
    return
  }

  if(event.shiftKey)
  {
    if(currentNum <= 5)
    {
      updateCurrentNum(0)
    }else
    {
      updateCurrentNum(currentNum - 5)
    }
  }else
  {
    updateCurrentNum(currentNum - 1)
  }

  localStorage.setItem('number',JSON.stringify(currentNum - (event.shiftKey ? 5 : 1)))

}
return (
  <div style={{ height: "300px", width: "100%", background: "black" }}>
    <Particles
      id="tsparticles"
      init={particlesInit}
      options={{
        particles: {
          number: { value: 50 },
          size: { value: 3 },
          move: { enable: true, speed: 1 },
          links: { enable: true, color: "#ffffff", distance: 150 },
        }
      }}
    />
  </div>
);
}

export default App;
