import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

//currentNum = parseInt(localStorage.getItem('number')) || 0



/*
function MyButton() {
  const num = parseInt(localStorage.getItem('number')) || 0
  localStorage.setItem('number',JSON.stringify(num + 1))
  return (
    <button>I'm a button</button>
  );
}
  */
//const num = parseInt(localStorage.getItem('number'));
function App() {
  const [currentNum, updateCurrentNum] = useState(parseInt(localStorage.getItem('number')) || 0);

function incrementNumber() {
  //const num = parseInt(localStorage.getItem('number')) || 0
  updateCurrentNum(currentNum + 1)
  localStorage.setItem('number',JSON.stringify(currentNum + 1))

}
function decrementNumber() {
  //const num = parseInt(localStorage.getItem('number')) || 0
  if(currentNum <= 0)
  {
    return
  }
  updateCurrentNum(currentNum - 1)
  localStorage.setItem('number',JSON.stringify(currentNum - 1))

}
  return (
    <div className="App">
      <title>First web page!</title>
      <h1 className='Menu-bar'>Some sample text for a page</h1>

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>

      <button onClick={incrementNumber}>{currentNum}</button>
    </div>
  );
}

export default App;
