import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

import { Particles } from '@tsparticles/react';
import { loadFull } from '@tsparticles/react';
import { initParticlesEngine } from '@tsparticles/react';
//currentNum = parseInt(localStorage.getItem('number')) || 0



/*
function MyButton() {
  const num = parseInt(localStorage.getItem('number')) || 0
  localStorage.setItem('number',JSON.stringify(num + 1))
  return (
    <button>I'm a button</button>
  );
}
  */
//const num = parseInt(localStorage.getItem('number'));
function App() {
  const particlesInit = async (main) => {
    // Load the full tsparticles package
    await loadFull(main);
  };
  const [currentNum, updateCurrentNum] = useState(parseInt(localStorage.getItem('number')) || 0);

function incrementNumber(event) {


    if(event.shiftKey)
    {
      updateCurrentNum(currentNum + 5)
    }else
    {
      updateCurrentNum(currentNum + 1)
    }
  //const num = parseInt(localStorage.getItem('number')) || 0

  localStorage.setItem('number',JSON.stringify(currentNum + (event.shiftKey * 5) + (!event.shiftKey * 1)))

}
function decrementNumber(event) {

  //const num = parseInt(localStorage.getItem('number')) || 0
  if(currentNum <= 0)
  {
    return
  }

  if(event.shiftKey)
  {
    if(currentNum <= 5)
    {
      updateCurrentNum(0)
    }else
    {
      updateCurrentNum(currentNum - 5)
    }
  }else
  {
    updateCurrentNum(currentNum - 1)
  }

  localStorage.setItem('number',JSON.stringify(currentNum - (event.shiftKey * 5) - (!event.shiftKey * 1)))

}
  return (
    <div className="App">
      <title>First web page!</title>
      <h1 className='Menu-bar'>Some sample text for a page</h1>

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>


      <p>
      <button onClick={incrementNumber}>+</button>
      <text>    {currentNum}    </text>

      <button onClick={decrementNumber}>-</button>
      </p>
      <input type='text' onChange={incrementNumber}></input>
      <Particles init={initParticlesEngine} options={{
        fullScreen: {
          enable: true,
          zIndex: -1,
        },
        particles: {
          number: {
            value: 50,
          },
          size: {
            value: 3,
          },
          move: {
            enable: true,
            speed: 1,
          },
          links: {
            enable: true,
            color: "#ffffff",
            distance: 150,
          },
        },
        background: {
          color: "#0d47a1",
        },
      }}></Particles>
      <p>after</p>
    </div>
  );
}

export default App;
